            <div class="form-floating col-md-3 mb-3 gx-1">
                <input type="text" id="location" formControlName="location" class="form-control" />
                <label for="location">Location:</label>
            </div>
        </div>
        <div class="form-group row">
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="number" id="pricePerNight" formControlName="pricePerNight" class="form-control" />
                <label for="pricePerNight">Price per night:</label>
            </div>
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="number" id="bedrooms" formControlName="bedrooms" class="form-control" />
                <label for="bedrooms">Bedrooms:</label>
            </div>
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="number" id="bathrooms" formControlName="bathrooms" class="form-control" />
                <label for="bathrooms">Bathrooms:</label>
            </div>
        </div>
        <div class="form-group row">
            <div class="form-floating gx-1 mb-3">
                <textarea id="description" formControlName="description" class="form-control" style="height: 100px"></textarea>
                <label for="description">Description:</label>
            </div>
        </div>
        <div class="form-group row">
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="text" id="houseImageUrl" formControlName="houseImageUrl" class="form-control" />
                <label for="houseImageUrl">House Image URL:</label>
            </div>
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="text" id="bedroomImageUrl" formControlName="bedroomImageUrl" class="form-control" />
                <label for="bedroomImageUrl">Bedroom Image URL:</label>
            </div>
            <div class="form-floating col-md-4 mb-3 gx-1">
                <input type="text" id="bathroomImageUrl" formControlName="bathroomImageUrl" class="form-control" />
                <label for="bathroomImageUrl">Bathroom Image URL:</label>
            </div>
        </div>
        <button class='btn btn-primary rounded-pill' type="submit" [disabled]="houseForm.invalid">
            {{ isEditMode ? 'Update' : 'Create'}} House
        </button>
        <button class='btn btn-secondary rounded-pill' (click)="backToHouses()">Cancel</button>
    </form>
</div>

<div class="col-md-5 shadow-sm overflow-hidden" style="border-radius: 15px">
    <ng-container *ngIf="!house.BedroomImageUrl && !house.BathroomImageUrl">
        <img [src]="house.HouseImageUrl" class="card-img-top" style="border-radius: 15px" alt="{{ house.Title }}" />
    </ng-container>
    <ng-container *ngIf="house.BedroomImageUrl || house.BathroomImageUrl">
        <div id="carouselImages" class="carousel slide" data-bs-ride="carousel" style="border-radius: 15px; overflow: hidden;">
            <div class="carousel-indicators">
                <button type="button" [attr.data-bs-target]="'#carouselImages'" [attr.data-bs-slide-to]="0" class="active" aria-current="true" aria-label="Slide 1"></button>
                <ng-container *ngIf="house.BedroomImageUrl || house.BathroomImageUrl">
                    <button type="button" [attr.data-bs-target]="'#carouselImages'" [attr.data-bs-slide-to]="1" aria-label="Slide 2"></button>
                </ng-container>
                <ng-container *ngIf="house.BedroomImageUrl && house.BathroomImageUrl">
                    <button type="button" [attr.data-bs-target]="'#carouselImages'" [attr.data-bs-slide-to]="2" aria-label="Slide 3"></button>
                </ng-container>
            </div>
            <div class="carousel-inner">
                <div class="carousel-item" *ngFor="let image of [house.HouseImageUrl, house.BedroomImageUrl, house.BathroomImageUrl]; let i = index" [class.active]="i === 0">
                    <img [src]="image" alt="{{ house.Title }}" class="card-img-top" style="height: 300px;">
                </div>
            </div>
            <button class="carousel-control-prev" type="button" [attr.data-bs-target]="'#carouselImages'" data-bs-slide="prev" style="display: flex; align-items: center;">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
            </button>
            <button class="carousel-control-next" type="button" [attr.data-bs-target]="'#carouselImages'" data-bs-slide="next" style="display: flex; align-items: center;">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
            </button>
        </div>
    </ng-container>
    <div class="card-body">
        <div class="d-flex justify-content-between mt-2">
            <h5 class="card-title text-start">{{ house.Title }}</h5>
            <h5 class="text-nowrap">{{ house.PricePerNight | currency: 'NOK':'symbol-narrow' }} / night</h5>
        </div>
        <hr />
        <dl class="row">
            <dt class="col-sm-4"><strong>Host</strong></dt>
            <dd class="col-sm-8">{{ house.User?.FirstName }} {{ house.User?.LastName }}</dd>

            <dt class="col-sm-4"><strong>Location</strong></dt>
            <dd class="col-sm-8">{{ house.Location }}</dd>

            <dt class="col-sm-4"><strong>Description</strong></dt>
            <dd class="col-sm-8">{{ house.Description }}</dd>

            <dt class="col-sm-4"><strong>House Image-url</strong></dt>
            <dd class="col-sm-8">{{ house.HouseImageUrl }}</dd>
        </dl>
        <span class="badge rounded-pill bg-light text-dark p-2">{{ house.Bedrooms }} Bedrooms</span>
        <span class="badge rounded-pill bg-light text-dark p-2">{{ house.Bathrooms }} Bathrooms</span>
    </div>
</div>
