import { Component, OnInit } from '@angular/core';
import { IHouse } from '../houses/house';
import { ActivatedRoute, Router } from '@angular/router';
import { HouseService } from '../houses/houses.service';

@Component({
  selector: 'app-house-details-component',
  templateUrl: './house-details.component.html',
  styleUrls: ['./house-details.component.css']
})

export class HouseDetailsComponent implements OnInit {
  viewTitle: string = 'Details';
  house!: IHouse;

  constructor(
    private _router: Router,
    private _houseService: HouseService,
    private activatedRoute: ActivatedRoute) {
    activatedRoute.params.subscribe((params) => {
      if (params.id)
        this.loadHouse(+params['id'])
    })
  }

  deleteHouse(house: IHouse): void {
    const confirmDelete = confirm(`Are you sure you want to delete "${house.Title}"?`);
    if (confirmDelete) {
      this._houseService.deleteHouse(house.HouseId)
        .subscribe(
          (response) => {
            if (response.success) {
              console.log(response.message);
            }
          },
          (error) => {
            console.error('Error deleting house:', error);
          });
    }
  }

  loadHouse(houseId: number) {
    this._houseService.getHouseById(houseId)
      .subscribe(
        (house: any) => {
          console.log('retrieved house: ', house);
          this.house = house;
        }, (error: any) => {
          console.error('Error loading house for details view:', error);
        }
      );
  }

  ngOnInit(): void {

  }
}
<div class="container">
    <img [src]="house.HouseImageUrl" alt="house.Title" />
    <div class="details">
        <span class="title">{{ house.Title }}</span>
        <div class="price">
            <span>{{ house.PricePerNight | convertToCurrency: 'kr' }} / night</span>
        </div>
        <div class="description">
            <span>{{ house.Description }}</span>
        </div>
        <div class="room-info">
            <span>{{ house.Bedrooms }} Bedrooms</span>
            <span>{{ house.Bathrooms }} Bathrooms</span>
        </div>
        <div class="location">
            <span>Location</span>
            <span>{{ house.Location }}</span>
        </div>
        <button class="btn btn-lg btn-primary rounded-pill" routerLink="/reservationform">Reserve House</button>
    </div>
</div>
<div class="actions">
    <button class="btn btn-sm btn-secondary rounded-pill my-1" [routerLink]="['/houseform', 'edit', house.HouseId, viewTitle]">Update</button>
    <button class="btn btn-sm btn-danger rounded-pill" (click)="deleteHouse(house)">Delete</button>
</div>
