import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ReservationService } from '../reservations/reservations.service';
import { IReservation } from '../reservations/reservation';
import { HouseService } from '../houses/houses.service';

@Component({
  selector: 'app-reservation-details-component',
  templateUrl: './reservation-details.component.html',
  styleUrls: ['./reservation-details.component.css']
})

export class ReservationDetailsComponent implements OnInit {
  viewTitle: string = 'Details';
  reservation!: IReservation;

  constructor(
    private _router: Router,
    private _reservationService: ReservationService,
    private _houseService: HouseService,
    private activatedRoute: ActivatedRoute) {
    activatedRoute.params.subscribe((params) => {
      if (params.id)
        this.loadReservation(+params['id'])
    })
  }

  deleteReservation(reservation: IReservation): void {
    const confirmDelete = confirm(`Are you sure you want to delete "${reservation.House?.Title}"?`);
    if (confirmDelete) {
      this._reservationService.deleteReservation(reservation.ReservationId)
        .subscribe(
          (response) => {
            if (response.success) {
              console.log(response.message);
            }
          },
          (error) => {
            console.error('Error deleting reservation:', error);
          });
    }
  }

  loadReservation(reservationId: number) {
    this._reservationService.getReservationById(reservationId)
      .subscribe(
        (reservation: any) => {
          console.log('retrieved reservation: ', reservation);
          this.reservation = reservation;
        }, (error: any) => {
          console.error('Error loading reservation for details view:', error);
        }
      );
  }

  setHouseForReservations() {
    this._houseService.getHouseById(this.reservation.HouseId).subscribe(house => {
      console.log('Retrieved house:', house);
      this.reservation.House = house;
      console.log('Reservation after setting House:', this.reservation);
    });
  }

  backToReservations() {
    this._router.navigate(['/reservations']);
  }

  ngOnInit(): void {

  }
}
<h3 class="my-4">
    Reservation {{reservation.ReservationId}}
</h3>

<div class="container">
    <div class="row gx-5 shadow-sm overflow-hidden" style="border-radius: 15px">
        <img class="img-fluid col-md-6 p-0" [src]="reservation.House?.HouseImageUrl" [alt]="reservation.House?.Title" />
        <dl class="row col-md-6 p-4">
            <h5>{{ reservation.House?.Title }}</h5>
            <dt class="col-sm-5"><strong>Date Created:</strong></dt>
            <dd class="col-sm-7">{{reservation.DateCreated | date: 'dd-MM-yyyy'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Check-In</strong></dt>
            <dd class="col-sm-7">{{reservation.CheckInDate | date: 'dd-MM-yyyy'}}</dd>

            <dt class="col-sm-5"><strong>Check-Out</strong></dt>
            <dd class="col-sm-7">{{reservation.CheckOutDate | date: 'dd-MM-yyyy'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Booking duration</strong></dt>
            <dd class="col-sm-7">{{reservation.BookingDuration}} days</dd>

            <dt class="col-sm-5"><strong>Price per night</strong> </dt>
            <dd class="col-sm-7">{{reservation.House?.PricePerNight ?? 0 | convertToCurrency: 'kr'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Total Price</strong> </dt>
            <dd class="col-sm-7"><strong>{{reservation.TotalPrice | convertToCurrency: 'kr'}}</strong></dd>
        </dl>
    </div>
</div>
<div class="mt-3">
    <a class="btn btn-outline-dark rounded-pill" (click)="backToReservations()">Back to Reservations</a>
    <a class="btn btn-secondary rounded-pill mx-2" [routerLink]="['/reservationform', 'edit', reservation.ReservationId, viewTitle]">Update</a>
    <a class="btn btn-danger rounded-pill" (click)="deleteReservation(reservation)">Delete</a>
</div>

<div class="container">
    <img [src]="reservation.House?.HouseImageUrl" [alt]="reservation.House?.Title" />
    <div class="details">
        <span class="title">{{ reservation.House?.Title }}</span>
        <div class="price">
            <span>{{ reservation.House?.PricePerNight ?? 0 | convertToCurrency: 'kr'}} / night</span>
        </div>
        <div class="description"><span>{{ reservation.House?.Description }}</span></div>
        <div class="room-info">
            <span>{{ reservation.House?.Bedrooms }} Bedrooms</span>
            <span>{{ reservation.House?.Bathrooms }} Bathrooms</span>
        </div>
        <div class="location">
            <span>Location</span>
            <span>{{ reservation.House?.Location }}</span>
        </div>
        <dl class="row col-md-6 p-4">
            <h5>{{ reservation.House?.Title }}</h5>
            <dt class="col-sm-5"><strong>Date Created:</strong></dt>
            <dd class="col-sm-7">{{reservation.DateCreated | date: 'dd-MM-yyyy'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Check-In</strong></dt>
            <dd class="col-sm-7">{{reservation.CheckInDate | date: 'dd-MM-yyyy'}}</dd>

            <dt class="col-sm-5"><strong>Check-Out</strong></dt>
            <dd class="col-sm-7">{{reservation.CheckOutDate | date: 'dd-MM-yyyy'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Booking duration</strong></dt>
            <dd class="col-sm-7">{{reservation.BookingDuration}} days</dd>

            <dt class="col-sm-5"><strong>Price per night</strong> </dt>
            <dd class="col-sm-7">{{reservation.House?.PricePerNight ?? 0 | convertToCurrency: 'kr'}}</dd>
            <hr />
            <dt class="col-sm-5"><strong>Total Price</strong> </dt>
            <dd class="col-sm-7"><strong>{{reservation.TotalPrice | convertToCurrency: 'kr'}}</strong></dd>
        </dl>
    </div>
</div>
