import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../authentication/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  errorMessage: string | null = null;

  constructor(private _formBuilder: FormBuilder,
    private router: Router,
    private authService: AuthService) {
      this.loginForm = _formBuilder.group( {
        Email: ['', Validators.required],
        ProvidedPassword: ['', Validators.required]
      });
    }

    onSubmit() {
      console.log("Login form submitted");
      console.log(this.loginForm);
      if (this.loginForm.invalid) {
        // Mark fields as touched to trigger validation messages
        this.loginForm.markAllAsTouched();
        return;
      }

      const credentials = this.loginForm.value;
      this.authService.login(credentials).subscribe(
        (response) => {
          if (response.success) {
            localStorage.setItem("userEmail", response.userEmail);
            this.router.navigate(["/"]);
            console.log("userEmail: ", response.userEmail);
          } else {
            // Handle unsuccessful login
            this.errorMessage = response.message;
          }
        },
        (error) => {
          // Handle HTTP error
          console.error("Error during login:", error);
          this.errorMessage = "An unexpected error occurred.";
        }
      );
    }
}
<div class="container p-5 shadow-sm col-md-5">
    <h2 class="mb-3">Login</h2>

    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
            <div class="form-floating mb-3">
                <input type="email" id="Email" formControlName="Email" class="form-control" />
                <label for="Email">Email</label>
                <span *ngIf="loginForm.get('houseImageUrl')?.hasError('required') && loginForm.get('houseImageUrl')?.touched" class="text-danger">
                    * Email is required.
                </span>
            </div>
            <div class="form-floating mb-3">
                <input type="password" id="ProvidedPassword" formControlName="ProvidedPassword" class="form-control" />
                <label for="Password">Password</label>
                <span *ngIf="loginForm.get('houseImageUrl')?.hasError('required') && loginForm.get('houseImageUrl')?.touched" class="text-danger">
                    * Password is required.
                </span>
            </div>
        </div>
        <button class="btn btn-primary rounded-pill w-100 my-2" type="submit" [disabled]="loginForm.invalid">Log in</button>
    </form>
    <div class="my-4">
        <p>Don't have an user? Register below</p>
        <button class="btn btn-secondary rounded-pill w-100" [routerLink]="['/register']">Register</button>
    </div>
</div>
