import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { HouseService } from './houses.service';

@Component({
  selector: "app-houseform-component",
  templateUrl: "./houseform.component.html"
})
export class HouseformComponent {
  houseForm: FormGroup;
  isEditMode: boolean = false;
  houseId: number = -1;

  constructor(
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute,
    private _houseService: HouseService
  ) {
    this.houseForm = _formbuilder.group({
      title: ['', [Validators.required, Validators.pattern('[0-9a-zA-ZæøåÆØÅ. \\-]{2,50}')]],
      description: ['', [Validators.required, Validators.minLength(10)]],
      houseImageUrl: ['', [Validators.required, Validators.pattern('[https?://\\S+|www\\.\\S+]{10,300}')]],
      bedroomImageUrl: ['', [Validators.pattern('[https?://\\S+|www\\.\\S+]{10,300}')]],
      bathroomImageUrl: ['', [Validators.pattern('[https?://\\S+|www\\.\\S+]{10,300}')]],
      location: ['', [Validators.required, Validators.pattern('^[A-ZÆØÅ][a-zAæøå]{1,25},\\s[A-ZÆØÅ][a-zA-Zæøå]{1,25}$')]],
      pricePerNight: [0, [Validators.required, Validators.min(0.01)]],
      bedrooms: [0, [Validators.required, Validators.min(0)]],
      bathrooms: [0, [Validators.required, Validators.min(0)]]
    });
  }

  onSubmit() {
    console.log("HouseCreate form submitted:");
    console.log(this.houseForm);
    const newHouse = this.houseForm.value;
    if (this.isEditMode) {
      console.log("HouseDetails: ", JSON.stringify(this.houseForm.value));
      this._houseService.updateHouse(this.houseId, newHouse)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/houses']);
          } else {
            console.log('House update failed');
          }
        });
    } else {
      console.log("HouseDetails: ", JSON.stringify(this.houseForm.value));
      this._houseService.createHouse(newHouse)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/houses']);
          }
          else {
            console.log('House creation failed');
          }
        });
    }
  }

  backToHouses() {
    this._route.params.subscribe(params => {
      if (params['view'] === 'Table' || params['view'] === 'Grid') {
        this._router.navigate(['/houses']);
      } else if (params['view'] === 'Details') {
        this._router.navigate(['/house-details/' + this.houseId]);
      }
    });
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false; // Create mode
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true; // Edit mode
        this.houseId = +params['id']; // Convert to number
        this.loadHouseForEdit(this.houseId);
      }
    });
  }

  loadHouseForEdit(houseId: number) {
    this._houseService.getHouseById(houseId)
      .subscribe(
        (house: any) => {
          console.log('retrieved house: ', house);
          this.houseForm.patchValue({
            title: house.Title,
            location: house.Location,
            pricePerNight: house.PricePerNight,
            bedrooms: house.Bedrooms,
            bathrooms: house.Bathrooms,
            description: house.Description,
            houseImageUrl: house.HouseImageUrl,
            bedroomImageUrl: house.BedroomImageUrl,
            bathroomImageUrl: house.BathroomImageUrl
          });
        }, (error: any) => {
          console.error('Error loading house for edit:', error);
        }
      );
  }
}

<h2>{{ isEditMode ? 'Update' : 'Create'}} House</h2>
<div class="p-4 shadow-sm" style="border-radius: 15px">
    <form [formGroup]="houseForm" (ngSubmit)="onSubmit()">
        <div class="form-group row">
          <div class="form-floating col-md-9 mb-3 gx-1">
            <input type="text" id="title" formControlName="title" class="form-control" />
            <label for="title">Title:</label>
            <div *ngIf="houseForm.get('title')?.hasError('required') && houseForm.get('title')?.touched" class="text-danger">
              * Title is required.
            </div>
            <div *ngIf="houseForm.get('title')?.hasError('pattern') && houseForm.get('title')?.touched" class="text-danger">
              * The Title must be numbers or letters and between 2 to 50 characters.
            </div>
          </div>
          <div class="form-floating col-md-3 mb-3 gx-1">
            <input type="text" id="location" formControlName="location" class="form-control" />
            <label for="location">Location:</label>
            <div *ngIf="houseForm.get('location')?.hasError('required') && houseForm.get('location')?.touched" class="text-danger">
              * Location is required.
            </div>
            <div *ngIf="houseForm.get('location')?.hasError('pattern') && houseForm.get('location')?.touched" class="text-danger">
              * E.g. 'Oslo, Norway'
            </div>
          </div>
        </div>
        <div class="form-group row">
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="number" id="pricePerNight" formControlName="pricePerNight" class="form-control" />
            <label for="pricePerNight">Price per night:</label>
            <div *ngIf="houseForm.get('pricePerNight')?.hasError('required') && houseForm.get('pricePerNight')?.touched" class="text-danger">
              * Price per night is required.
            </div>
            <div *ngIf="houseForm.get('pricePerNight')?.hasError('min') && houseForm.get('pricePerNight')?.touched" class="text-danger">
              * The price must be greater than 0.
            </div>
          </div>
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="number" id="bedrooms" formControlName="bedrooms" class="form-control" />
            <label for="bedrooms">Bedrooms:</label>
            <div *ngIf="houseForm.get('bedrooms')?.hasError('required') && houseForm.get('bedrooms')?.touched" class="text-danger">
              * Number of bedrooms is required.
            </div>
            <div *ngIf="houseForm.get('bedrooms')?.hasError('min') && houseForm.get('bedrooms')?.touched" class="text-danger">
              * The value must be a whole number and at least 1.
            </div>
          </div>
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="number" id="bathrooms" formControlName="bathrooms" class="form-control" />
            <label for="bathrooms">Bathrooms:</label>
            <div *ngIf="houseForm.get('bathrooms')?.hasError('required') && houseForm.get('bathrooms')?.touched" class="text-danger">
              * Number of bathrooms is required.
            </div>
            <div *ngIf="houseForm.get('bathrooms')?.hasError('min') && houseForm.get('bathrooms')?.touched" class="text-danger">
              * The value must be a whole number and at least 1.
            </div>
          </div>
        </div>
        <div class="form-group row">
          <div class="form-floating gx-1 mb-3">
            <textarea id="description" formControlName="description" class="form-control" style="height: 100px"></textarea>
            <label for="description">Description:</label>
            <div *ngIf="houseForm.get('description')?.hasError('required') && houseForm.get('description')?.touched" class="text-danger">
              * Description is required.
            </div>
            <div *ngIf="houseForm.get('description')?.hasError('minLength') && houseForm.get('description')?.touched" class="text-danger">
              * The description must be at least 50 characters long.
            </div>
          </div>
        </div>
        <div class="form-group row">
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="text" id="houseImageUrl" formControlName="houseImageUrl" class="form-control" />
            <label for="houseImageUrl">House Image URL:</label>
            <div *ngIf="houseForm.get('houseImageUrl')?.hasError('required') && houseForm.get('houseImageUrl')?.touched" class="text-danger">
              * House image url is required.
            </div>
            <div *ngIf="houseForm.get('houseImageUrl')?.hasError('pattern') && houseForm.get('houseImageUrl')?.touched" class="text-danger">
              * The Image-URL must be a link and be between 10 to 300 characters.
            </div>
          </div>
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="text" id="bedroomImageUrl" formControlName="bedroomImageUrl" class="form-control" />
            <label for="bedroomImageUrl">Bedroom Image URL:</label>
            <div *ngIf="houseForm.get('bedroomImageUrl')?.hasError('pattern') && houseForm.get('bedroomImageUrl')?.touched" class="text-danger">
              * The Image-URL must be a link and be between 10 to 300 characters.
            </div>
          </div>
          <div class="form-floating col-md-4 mb-3 gx-1">
            <input type="text" id="bathroomImageUrl" formControlName="bathroomImageUrl" class="form-control" />
            <label for="bathroomImageUrl">Bathroom Image URL:</label>
            <div *ngIf="houseForm.get('bathroomImageUrl')?.hasError('pattern') && houseForm.get('bathroomImageUrl')?.touched" class="text-danger">
              * The Image-URL must be a link and be between 10 to 300 characters.
            </div>
          </div>
        </div>
        <button class='btn btn-primary rounded-pill' type="submit" [disabled]="houseForm.invalid">
            {{ isEditMode ? 'Update' : 'Create'}} House
        </button>
        <button class='btn btn-secondary rounded-pill' (click)="backToHouses()">Cancel</button>
    </form>
</div>
